// ============================================
// GENERATE WEEKLY SUMMARY
// ============================================

const analyses = $input.all().map(item => item.json);

if (analyses.length === 0) {
  return [{ json: { error: 'No analyses found for this week' } }];
}

// Group by ticker
const tickerMap = {};
analyses.forEach(a => {
  if (!tickerMap[a.ticker]) {
    tickerMap[a.ticker] = [];
  }
  tickerMap[a.ticker].push(a);
});

// Calculate summary for each ticker
const tickerSummaries = Object.entries(tickerMap).map(([ticker, records]) => {
  const bullishDays = records.filter(r => r.aggregated_signal === 'BULLISH').length;
  const bearishDays = records.filter(r => r.aggregated_signal === 'BEARISH').length;
  const avgConfidence = records.reduce((sum, r) => sum + (r.aggregated_confidence || 0), 0) / records.length;

  let weeklySignal = 'NEUTRAL';
  if (bullishDays > bearishDays && bullishDays >= records.length / 2) weeklySignal = 'BULLISH';
  if (bearishDays > bullishDays && bearishDays >= records.length / 2) weeklySignal = 'BEARISH';

  return {
    ticker,
    weekly_signal: weeklySignal,
    bullish_days: bullishDays,
    bearish_days: bearishDays,
    avg_confidence: Math.round(avgConfidence),
    total_analyses: records.length
  };
});

// Sort by confidence
tickerSummaries.sort((a, b) => b.avg_confidence - a.avg_confidence);

// Top bullish and bearish
const topBullish = tickerSummaries.filter(t => t.weekly_signal === 'BULLISH').slice(0, 5);
const topBearish = tickerSummaries.filter(t => t.weekly_signal === 'BEARISH').slice(0, 5);

